from flask import Flask, request, jsonifyfrom db import get_teacher, get_max_studapp = Flask(__name__)@app.route('/', methods=['GET'])def test():    return 'hello world'@app.route('/test/<string:name>')def test2(name):    # format    v1 = 'hello {}'.format(name)    v2 = 'hello %s' % name    v3 = f'hello {name}'    v4 = 'hello' + name    pass@app.route('/test3')def test3():    name = request.args.get('name', 'qww')    # name = request.json.get('name', 'qww')    return 'hello ' + name@app.route('/teacher/<string:work_num>')def get_teacher_by_work_num(work_num):    teacher = get_teacher(work_num)    return jsonify(teacher)@app.route('/teacher2')def get_teacher_by_work_name():    name = request.args.get('work_name', '张三')    teacher = get_teacher(name)    return jsonify(teacher)@app.route('/getmaxstud')def get_max_student():    data = get_max_stud()    return jsonify(data)if __name__ == '__main__':    app.run('127.0.0.1', 5001)